import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { BookOpen, ExternalLink, Lightbulb, AlertTriangle, AlertCircle } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { api } from "@/lib/api";
import type { Topic } from "@shared/schema";

export default function Learn() {
  const [selectedTopic, setSelectedTopic] = useState<string>("");

  const { data: topics = [], isLoading, error } = useQuery<Topic[]>({
    queryKey: ["/api/topics"],
  });

  const currentTopic = topics.find((t) => t.id === selectedTopic);

  const lesson = {
    title: "Understanding Database Normalization",
    sections: [
      {
        heading: "What is Normalization?",
        content:
          "Database normalization is the process of organizing data to minimize redundancy and improve data integrity. It involves dividing large tables into smaller ones and defining relationships between them.",
      },
      {
        heading: "First Normal Form (1NF)",
        content:
          "A table is in 1NF if it contains only atomic values (no repeating groups) and each column contains values of a single type.",
      },
      {
        heading: "Second Normal Form (2NF)",
        content:
          "A table is in 2NF if it's in 1NF and all non-key attributes are fully dependent on the primary key.",
      },
    ],
    commonMistakes: [
      "Confusing partial dependency with transitive dependency",
      "Not identifying all candidate keys before normalizing",
    ],
    citations: ["Database Systems Textbook - Chapter 7", "PYQ_2023_Q4.pdf"],
    confidence: "High" as const,
  };

  if (error) {
    return (
      <div className="mx-auto max-w-4xl space-y-8">
        <div>
          <h1 className="text-4xl font-bold">Learn</h1>
          <p className="mt-2 text-muted-foreground">
            AI-generated micro-lessons with step-by-step explanations
          </p>
        </div>
        <Card>
          <CardContent className="py-12 text-center">
            <AlertCircle className="mx-auto h-12 w-12 text-destructive" />
            <h3 className="mt-4 text-lg font-medium">Error loading topics</h3>
            <p className="mt-2 text-sm text-muted-foreground">
              {error?.message || "Failed to load data. Please try again."}
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="mx-auto max-w-4xl space-y-8">
        <div>
          <h1 className="text-4xl font-bold">Learn</h1>
          <p className="mt-2 text-muted-foreground">
            AI-generated micro-lessons with step-by-step explanations
          </p>
        </div>
        <Card>
          <CardHeader>
            <Skeleton className="h-6 w-64" />
            <Skeleton className="h-4 w-48" />
          </CardHeader>
          <CardContent className="space-y-4">
            <Skeleton className="h-20 w-full" />
            <Skeleton className="h-20 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="mx-auto max-w-4xl space-y-8">
      <div>
        <h1 className="text-4xl font-bold">Learn</h1>
        <p className="mt-2 text-muted-foreground">
          AI-generated micro-lessons with step-by-step explanations
        </p>
      </div>

      <div className="flex items-center gap-4">
        <div className="flex-1">
          <Select value={selectedTopic} onValueChange={setSelectedTopic}>
            <SelectTrigger data-testid="select-topic">
              <SelectValue placeholder="Select a topic to learn" />
            </SelectTrigger>
            <SelectContent>
              {topics.map((topic) => (
                <SelectItem key={topic.id} value={topic.id}>
                  {topic.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      {currentTopic ? (
        <div className="space-y-6">
          <Card data-testid="card-lesson">
            <CardHeader>
              <div className="flex items-start justify-between gap-4">
                <div className="space-y-1">
                  <CardTitle>{lesson.title}</CardTitle>
                  <CardDescription>Topic: {currentTopic.name}</CardDescription>
                </div>
                <div className="flex items-center gap-2">
                  <Badge variant={lesson.confidence === "High" ? "default" : "secondary"}>
                    {lesson.confidence} Confidence
                  </Badge>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-6">
              {lesson.sections.map((section, idx) => (
                <div key={idx} className="space-y-3">
                  <h3 className="text-lg font-semibold">{section.heading}</h3>
                  <p className="leading-relaxed text-card-foreground">
                    {section.content}
                  </p>
                  {idx < lesson.sections.length - 1 && <Separator className="mt-4" />}
                </div>
              ))}

              {lesson.commonMistakes.length > 0 && (
                <div className="mt-8 rounded-md bg-muted p-6">
                  <div className="flex items-start gap-3">
                    <AlertTriangle className="h-5 w-5 text-destructive" />
                    <div>
                      <h4 className="font-medium">Common Mistakes</h4>
                      <ul className="mt-2 space-y-1 text-sm text-muted-foreground">
                        {lesson.commonMistakes.map((mistake, idx) => (
                          <li key={idx}>‚Ä¢ {mistake}</li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </div>
              )}

              <div className="mt-6 rounded-md border p-4">
                <div className="flex items-start gap-3">
                  <Lightbulb className="h-5 w-5 text-primary" />
                  <div>
                    <h4 className="text-sm font-medium">Pro Tip</h4>
                    <p className="mt-1 text-sm text-muted-foreground">
                      Practice with real examples from PYQs to solidify your understanding
                    </p>
                  </div>
                </div>
              </div>

              {lesson.citations.length > 0 && (
                <div className="mt-6">
                  <h4 className="mb-3 text-sm font-medium text-muted-foreground">
                    Sources
                  </h4>
                  <div className="flex flex-wrap gap-2">
                    {lesson.citations.map((citation, idx) => (
                      <Badge
                        key={idx}
                        variant="outline"
                        className="gap-1"
                      >
                        <ExternalLink className="h-3 w-3" />
                        {citation}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}
            </CardContent>
          </Card>

          <div className="flex gap-4">
            <Button variant="outline" className="flex-1" data-testid="button-practice">
              Practice This Topic
            </Button>
            <Button className="flex-1" data-testid="button-next-lesson">
              Next Lesson
            </Button>
          </div>
        </div>
      ) : (
        <Card>
          <CardContent className="py-12 text-center">
            <BookOpen className="mx-auto h-12 w-12 text-muted-foreground" />
            <h3 className="mt-4 text-lg font-medium">Select a topic</h3>
            <p className="mt-2 text-sm text-muted-foreground">
              Choose a topic from the dropdown to start learning
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

server/seed.ts.content:
import { storage } from "./storage";

export async function seedDatabase(userId: string) {
  console.log("Seeding database with sample data...");

  // Create sample topics
  const topics = [
    { name: "Database Normalization", importanceScore: 9, masteryScore: 45 },
    { name: "SQL Joins", importanceScore: 8, masteryScore: 60 },
    { name: "Transaction Management", importanceScore: 7, masteryScore: 30 },
    { name: "Indexing and Performance", importanceScore: 8, masteryScore: 55 },
    { name: "NoSQL Databases", importanceScore: 6, masteryScore: 70 },
    { name: "Data Structures - Arrays", importanceScore: 9, masteryScore: 80 },
    { name: "Data Structures - Trees", importanceScore: 8, masteryScore: 50 },
    { name: "Algorithms - Sorting", importanceScore: 9, masteryScore: 65 },
    { name: "Dynamic Programming", importanceScore: 10, masteryScore: 35 },
    { name: "System Design Basics", importanceScore: 7, masteryScore: 40 },
  ];

  for (const topicData of topics) {
    await storage.createTopic({
      userId,
      ...topicData,
    });
  }

  // Create a study plan
  const allTopics = await storage.getTopicsByUserId(userId);
  const examDate = new Date();
  examDate.setDate(examDate.getDate() + 60); // 60 days from now

  await storage.createStudyPlan({
    userId,
    startDate: new Date().toISOString(),
    endDate: examDate.toISOString(),
    examType: "SEE (Semester End Exam)",
    blocks: [
      {
        id: "block-1",
        topicId: allTopics[0].id,
        topicName: allTopics[0].name,
        activity: "learn",
        duration: 60,
        scheduledFor: new Date().toISOString(),
        completed: false,
      },
      {
        id: "block-2",
        topicId: allTopics[1].id,
        topicName: allTopics[1].name,
        activity: "practice",
        duration: 45,
        scheduledFor: new Date().toISOString(),
        completed: false,
      },
    ],
    weeklyGoal: "Study 20 hours per week with focus on weak topics",
  });

  // Create sample practice questions
  await storage.createPracticeQuestion({
    userId,
    topicId: allTopics[0].id,
    question:
      "Explain the difference between DELETE, TRUNCATE, and DROP commands in SQL. Provide examples and discuss when each should be used.",
    hint: "Think about what each command does to the table structure and data",
    steps: [
      "DELETE removes rows from a table based on a condition",
      "TRUNCATE removes all rows from a table but keeps the structure",
      "DROP removes the entire table including structure and data",
      "DELETE can be rolled back, TRUNCATE cannot in most databases",
      "DROP is irreversible and removes the table completely",
    ],
    fullSolution:
      "DELETE is a DML command that removes specific rows based on a WHERE clause and can be rolled back. TRUNCATE is a DDL command that removes all rows but preserves the table structure, is faster than DELETE, and cannot be rolled back. DROP is a DDL command that removes the entire table including its structure, data, and associated objects like indexes, and is irreversible. Use DELETE for selective row removal, TRUNCATE for quick table cleanup, and DROP when you want to remove the table entirely.",
    rubric: "5 points for explaining each command, 5 points for examples, 5 points for use cases",
    difficulty: "medium",
    citations: ["Database Systems Textbook - Chapter 7", "SQL Documentation"],
    confidence: "High",
  });

  // Create a mock exam
  await storage.createMockExam({
    userId,
    type: "SEE",
    title: "Database Management Systems - Mock Exam 1",
    duration: 120,
    totalMarks: 100,
    questions: [
      {
        id: "q1",
        question: "Define database normalization and explain its importance.",
        marks: 10,
        rubric: "Definition: 5 marks, Importance: 5 marks",
        topicId: allTopics[0].id,
      },
      {
        id: "q2",
        question: "Write SQL queries to demonstrate INNER JOIN, LEFT JOIN, and RIGHT JOIN.",
        marks: 15,
        rubric: "Each query: 5 marks",
        topicId: allTopics[1].id,
      },
      {
        id: "q3",
        question: "Explain ACID properties with examples.",
        marks: 10,
        rubric: "Each property with example: 2.5 marks",
        topicId: allTopics[2].id,
      },
      {
        id: "q4",
        question: "Compare B-tree and Hash indexing. When would you use each?",
        marks: 10,
        rubric: "Comparison: 5 marks, Use cases: 5 marks",
        topicId: allTopics[3].id,
      },
      {
        id: "q5",
        question: "Design a database schema for an e-commerce application.",
        marks: 15,
        rubric: "Tables: 5 marks, Relationships: 5 marks, Normalization: 5 marks",
        topicId: allTopics[0].id,
      },
    ],
    instructions:
      "Answer all questions. Show your work for full credit. Time limit: 2 hours.",
  });

  // Create sample flashcards
  const flashcardData = [
    {
      front: "What is normalization?",
      back: "The process of organizing data to minimize redundancy and improve data integrity by dividing large tables into smaller ones and defining relationships.",
    },
    {
      front: "What does ACID stand for?",
      back: "Atomicity, Consistency, Isolation, Durability - the four key properties of database transactions.",
    },
    {
      front: "Difference between INNER JOIN and OUTER JOIN?",
      back: "INNER JOIN returns only matching rows from both tables. OUTER JOIN returns all rows from one or both tables, with NULLs for non-matching rows.",
    },
  ];

  for (const card of flashcardData) {
    const nextReview = new Date();
    nextReview.setDate(nextReview.getDate() + 1);

    await storage.createFlashcard({
      userId,
      topicId: allTopics[0].id,
      front: card.front,
      back: card.back,
      nextReviewAt: nextReview.toISOString(),
      easinessFactor: 2.5,
      interval: 1,
      repetitions: 0,
    });
  }

  console.log("‚úì Database seeded successfully!");
  console.log(`  - ${topics.length} topics created`);
  console.log("  - 1 study plan created");
  console.log("  - 1 practice question created");
  console.log("  - 1 mock exam created");
  console.log(`  - ${flashcardData.length} flashcards created`);
}

client/src/pages/Ingest.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Upload, FileText, Loader2, CheckCircle2, AlertCircle } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import type { Corpus, Topic } from "@shared/schema";

export default function Ingest() {
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const { toast } = useToast();

  const { data: topics = [], isLoading, error } = useQuery<Topic[]>({
    queryKey: ["/api/topics"],
  });

  const uploadMutation = useMutation({
    mutationFn: async (files: FileList) => {
      const formData = new FormData();
      Array.from(files).forEach((file) => {
        formData.append("files", file);
      });
      return api.ingest.upload(formData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/topics"] });
      toast({
        title: "Upload complete",
        description: "Documents processed and topics extracted",
      });
      setUploading(false);
      setUploadProgress(0);
    },
    onError: () => {
      toast({
        title: "Upload failed",
        description: "Please try again",
        variant: "destructive",
      });
      setUploading(false);
      setUploadProgress(0);
    },
  });

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files || files.length === 0) return;

    setUploading(true);
    setUploadProgress(0);

    const interval = setInterval(() => {
      setUploadProgress((prev) => {
        if (prev >= 90) {
          clearInterval(interval);
          return prev;
        }
        return prev + 10;
      });
    }, 200);

    uploadMutation.mutate(files);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length === 0) return;

    const input = document.createElement("input");
    input.type = "file";
    input.files = files;
    const event = new Event("change", { bubbles: true });
    Object.defineProperty(event, "target", { writable: false, value: input });
    handleFileSelect(event as any);
  };

  if (error) {
    return (
      <div className="space-y-8">
        <div>
          <h1 className="text-4xl font-bold">Document Ingestion</h1>
          <p className="mt-2 text-muted-foreground">
            Upload PYQs, teacher notes, and mock papers for AI-powered analysis
          </p>
        </div>
        <Card>
          <CardContent className="py-12 text-center">
            <AlertCircle className="mx-auto h-12 w-12 text-destructive" />
            <h3 className="mt-4 text-lg font-medium">Error loading topics</h3>
            <p className="mt-2 text-sm text-muted-foreground">
              {error?.message || "Failed to load data. Please try again."}
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-4xl font-bold">Document Ingestion</h1>
        <p className="mt-2 text-muted-foreground">
          Upload PYQs, teacher notes, and mock papers for AI-powered analysis
        </p>
      </div>

      <Card data-testid="card-upload">
        <CardHeader>
          <CardTitle>Upload Documents</CardTitle>
          <CardDescription>
            Supports PDF and image files. OCR will extract text automatically.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div
            className="rounded-md border-2 border-dashed border-muted-foreground/25 p-12 text-center hover-elevate cursor-pointer transition-colors hover:border-primary/50"
            onDrop={handleDrop}
            onDragOver={(e) => e.preventDefault()}
            onClick={() => document.getElementById("file-input")?.click()}
            data-testid="dropzone-upload"
          >
            <input
              id="file-input"
              type="file"
              multiple
              accept=".pdf,.png,.jpg,.jpeg"
              className="hidden"
              onChange={handleFileSelect}
              data-testid="input-file"
            />

            {uploading ? (
              <div className="space-y-4">
                <Loader2 className="mx-auto h-12 w-12 animate-spin text-primary" />
                <p className="text-sm font-medium">Processing documents...</p>
                <Progress value={uploadProgress} className="mx-auto w-64" />
              </div>
            ) : (
              <>
                <Upload className="mx-auto h-12 w-12 text-muted-foreground" />
                <p className="mt-4 text-sm font-medium">
                  Drop files here or click to browse
                </p>
                <p className="mt-2 text-sm text-muted-foreground">
                  PDF, PNG, JPG up to 10MB each
                </p>
              </>
            )}
          </div>
        </CardContent>
      </Card>

      {isLoading ? (
        <Card>
          <CardHeader>
            <Skeleton className="h-6 w-40" />
            <Skeleton className="h-4 w-64" />
          </CardHeader>
          <CardContent className="space-y-4">
            <Skeleton className="h-20 w-full" />
            <Skeleton className="h-20 w-full" />
          </CardContent>
        </Card>
      ) : topics.length > 0 && (
        <Card data-testid="card-topics">
          <CardHeader>
            <CardTitle>Extracted Topics</CardTitle>
            <CardDescription>
              Topics identified from your uploaded documents
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {topics.map((topic) => (
                <div
                  key={topic.id}
                  className="flex items-center justify-between rounded-md border p-4"
                  data-testid={`topic-${topic.id}`}
                >
                  <div className="flex items-center gap-4">
                    <div className="flex h-10 w-10 items-center justify-center rounded-md bg-primary/10 text-primary">
                      <FileText className="h-5 w-5" />
                    </div>
                    <div>
                      <p className="font-medium">{topic.name}</p>
                      <div className="mt-1 flex items-center gap-2">
                        <Badge variant="outline" className="text-xs">
                          Importance: {topic.importanceScore}/10
                        </Badge>
                        <Badge variant="outline" className="text-xs">
                          Mastery: {topic.masteryScore}%
                        </Badge>
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center gap-2">
                    {topic.masteryScore >= 70 ? (
                      <CheckCircle2 className="h-5 w-5 text-chart-2" />
                    ) : topic.masteryScore >= 40 ? (
                      <AlertCircle className="h-5 w-5 text-chart-3" />
                    ) : (
                      <AlertCircle className="h-5 w-5 text-destructive" />
                    )}
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {topics.length === 0 && (
        <Card>
          <CardContent className="py-12 text-center">
            <FileText className="mx-auto h-12 w-12 text-muted-foreground" />
            <h3 className="mt-4 text-lg font-medium">No topics yet</h3>
            <p className="mt-2 text-sm text-muted-foreground">
              Upload your first document to get started with AI-powered topic extraction
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

